[asy]import graph;
draw(Circle((0,0),42.4),linewidth(1));
draw((0,0)--(30,30),linewidth(2));
draw((0,0)--(30,-30),linewidth(2));
label("$A$",(30,30),E);
label("$B$",(30,-30),E);
label("4''",(15,15),NW);
draw((3,3)--(6,0));
draw((6,0)--(3,-3));

[/asy]

